const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component '" + name + "' was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`If you are using the SDK in a .NET Core application and make use of either the Microsoft Dependency Injection or Configuration libraries you can use the `}<a parentName="p" {...{
        "href": "https://www.nuget.org/packages/CheckoutSDK.Extensions.Microsoft"
      }}>{`CheckoutSDK.Extensions.Microsoft`}</a>{` package.`}</p>
    <h2 {...{
      "id": "dependency-injection"
    }}>{`Dependency Injection`}</h2>
    <p>{`With the above package installed, register SDK with the built-in DI container in `}<inlineCode parentName="p">{`Startup.cs`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`public void ConfigureServices(IServiceCollection services)
{
    // ...
    var configuration = new CheckoutConfiguration("sk_70d144d5-92bd-4040-83cf-faeb978b3d75", useSandbox: true);
    services.AddCheckoutSdk(configuration);    
}
`}</code></pre>
    <p>{`Then take a dependency on `}<inlineCode parentName="p">{`ICheckoutApi`}</inlineCode>{` in your class constructor:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`public class CheckoutController : ControllerBase
{
    private readonly ICheckoutApi _checkoutApi;

    public CheckoutController(ICheckoutApi checkoutApi)
    {
        _checkoutApi = checkoutApi ?? throw new ArgumentNullException(nameof(checkoutApi));
    }

    // etc.
}
`}</code></pre>
    <p>{`If you wish to override some of the [`}{`[Architecture|SDK's dependencies]`}{`] you can do this by registering the overrides after calling `}<inlineCode parentName="p">{`AddCheckoutSdk`}</inlineCode>{` for example:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`var configuration = new CheckoutConfiguration("sk_70d144d5-92bd-4040-83cf-faeb978b3d75", useSandbox: true);
services.AddCheckoutSdk(configuration);    
services.AddTransient<IHttpClientFactory, MyCustomHttpClientFactory>();
`}</code></pre>
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <p>{`If you would prefer to configure the SDK using a JSON settings file or Environment Variables, we recommend using `}<a parentName="p" {...{
        "href": "https://github.com/aspnet/Configuration"
      }}>{`.NET Core Configuration`}</a>{` and providing your `}<inlineCode parentName="p">{`IConfiguration`}</inlineCode>{` when calling `}<inlineCode parentName="p">{`AddCheckoutSdk`}</inlineCode>{`:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`public class Startup
{
    public Startup(IConfiguration configuration, IHostingEnvironment env)
    {
        Configuration = configuration;
    }

    public IConfiguration Configuration { get; }

    public void ConfigureServices(IServiceCollection services)
    {
        // ...
        services.AddCheckoutSdk(Configuration);    
    }
}
`}</code></pre>
    <h3 {...{
      "id": "json"
    }}>{`JSON`}</h3>
    <p>{`If you are using the JSON file Configuration Provider, you can then add your Checkout credentials to your `}<inlineCode parentName="p">{`appsettings.json`}</inlineCode>{` file:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "Checkout": {
    "UseSandbox": true,
    "SecretKey" : "sk_07fa5e52-3971-4bab-ae6b-a8e26007fccc"
  }
}
`}</code></pre>
    <h3 {...{
      "id": "environment-variables"
    }}>{`Environment Variables`}</h3>
    <p>{`Alternatively, if using the Environment Variable Configuration Provider, set the following environment variables:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`Checkout__UseSandbox`}</inlineCode></li>
      <li parentName="ul"><inlineCode parentName="li">{`Checkout__SecretKey`}</inlineCode></li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
