  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'errors',
	title: 'Error Handling'
};
export const metadata = {
  "id": "errors",
  "isDocsHomePage": false,
  "title": "Error Handling",
  "description": "CheckoutApiException",
  "source": "@site/docs/errors.md",
  "permalink": "/checkout-sdk-net/errors",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Testing",
    "permalink": "/checkout-sdk-net/testing"
  },
  "next": {
    "title": "Logging",
    "permalink": "/checkout-sdk-net/logging"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'CheckoutApiException',
  id: 'checkoutapiexception',
  children: [{
    value: 'Validation Errors (HTTP 422)',
    id: 'validation-errors-http-422',
    children: []
  }, {
    value: 'Resource Not Found (HTTP 404)',
    id: 'resource-not-found-http-404',
    children: []
  }]
}, {
  value: 'How errors are determined',
  id: 'how-errors-are-determined',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component '" + name + "' was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "checkoutapiexception"
    }}>{`CheckoutApiException`}</h2>
    <p>{`With the exception of the cases below, the SDK will throw a `}<inlineCode parentName="p">{`CheckoutApiException`}</inlineCode>{` if a non-successful HTTP status code is returned. `}</p>
    <h3 {...{
      "id": "validation-errors-http-422"
    }}>{`Validation Errors (HTTP 422)`}</h3>
    <p>{`If you send invalid data to our APIs we return a `}<inlineCode parentName="p">{`HTTP 422`}</inlineCode>{` response with a JSON error similar to the following:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "request_id": "b20f81ac-e6e7-40a2-b048-92e319da030c",
  "error_type": "request_invalid",
  "error_codes": [
    "payment_source_required"
  ]
}
`}</code></pre>
    <p>{`The SDK will throw a `}<inlineCode parentName="p">{`CheckoutValidationException`}</inlineCode>{` which can be caught to obtain the underlying validation error details:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-jsx"
      }}>{`try
{
    var apiResponse = await api.Payments.RequestAsync(paymentRequest);
}
catch (CheckoutValidationException validEx)
{
    return ShowError(validEx.Error);
}

`}</code></pre>
    <h3 {...{
      "id": "resource-not-found-http-404"
    }}>{`Resource Not Found (HTTP 404)`}</h3>
    <p>{`In the event of a resource not being present the SDK will throw a `}<inlineCode parentName="p">{`CheckoutResourceNotFoundException`}</inlineCode>{`.`}</p>
    <h2 {...{
      "id": "how-errors-are-determined"
    }}>{`How errors are determined`}</h2>
    <p>{`The errors above are triggered by status codes that do not fall in the 20X Status codes, or by validation issues. This means that statuses like a 202, 204 will not throw an exception`}</p>
    <div {...{
      "className": "admonition admonition-tip alert alert--success"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.5 0C3.48 0 1 2.19 1 5c0 .92.55 2.25 1 3 1.34 2.25 1.78 2.78 2 4v1h5v-1c.22-1.22.66-1.75 2-4 .45-.75 1-2.08 1-3 0-2.81-2.48-5-5.5-5zm3.64 7.48c-.25.44-.47.8-.67 1.11-.86 1.41-1.25 2.06-1.45 3.23-.02.05-.02.11-.02.17H5c0-.06 0-.13-.02-.17-.2-1.17-.59-1.83-1.45-3.23-.2-.31-.42-.67-.67-1.11C2.44 6.78 2 5.65 2 5c0-2.2 2.02-4 4.5-4 1.22 0 2.36.42 3.22 1.19C10.55 2.94 11 3.94 11 5c0 .66-.44 1.78-.86 2.48zM4 14h5c-.23 1.14-1.3 2-2.5 2s-2.27-.86-2.5-2z"
              }}></path></svg></span>{`tip`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`It's important to understand that Declines or 3D Secure responses do not throw an exception since the status code associated with them is in the 20X range. In the Payments section you will see some examples of best practices when it comes to handling responses.`}</p></div></div>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
